from PyQt5 import QtCore, QtGui, QtWidgets
from pyswip import Prolog
from graphviz import Digraph
class RoomController:
    def __init__(self):
        self.prolog_instance = Prolog()
        self.prolog_instance.consult('C:/Users/jmlma/Documents/GitHub/arbol-genealogico-prolog/smart-hub-prolog/proyectofinal.pl')
        self.graph = Digraph()


    def cantidad_puertas(self):
        puertas = 0
        query2 = self.prolog_instance.query("sensor(movimiento,habitacion)")
        for puerta in query2:
            print(puerta)
            puertas = puertas+1
        result = bool(query2)
        return result

    def accionar_puerta_ventana(self, relacion, codigo, habitacion):
        query = self.prolog_instance.query(relacion + "(" + codigo +","+ habitacion +")")
        result = str(bool(query))
        return result


